        .byte [@27ad9@],[@27ada@]             ; left border
        .byte [@27adb@],[@27adc@],[@27add@],[@27ade@]     ; LIFE
        .byte [@27adf@],[@27ae0@],[@27ae1@],[@27ae2@],[@27ae3@],[@27ae4@],[@27ae5@],[@27ae6@],[@27ae7@],[@27ae8@],[@27ae9@],[@27aea@]
